package basics;

public class FloatAndDoubleDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float a=10.5f;//if f is not mentioned then it will give coompile time error
		System.out.println("Value of a= "+a);
		System.out.println("Size of Float = "+Float.SIZE+" "+"bits");
		double b=10.5;
		System.out.println("Value of a= "+b);
		System.out.println("Size of Double = "+Double.SIZE+" "+"bits");

	}

}
