package basics;

public class PreDecPostDec {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b,c,d;
		a=1;
		b=2;
		c=--b;//pre decrement
		d=a--;//post decrement
		c--;
		System.out.println("a = "+a);
		System.out.println("b = "+b);
		System.out.println("c = "+c);
		System.out.println("d = "+d);

	}

}
