package basics;

public class PreIncPostInc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b,c,d;
		a=1;
		b=2;
		c=++b;
		d=a++;
		c++;
		System.out.println("a = "+a);
		System.out.println("b = "+b);
		System.out.println("c = "+c);
		System.out.println("d = "+d);

	}

}
